"use strict";

// Node version of reading a wasm file into an array buffer.
var fs = require("fs");

var path = require("path");

module.exports = function readWasm() {
  return new Promise(function (resolve, reject) {
    var wasmPath = path.join(__dirname, "mappings.wasm");
    fs.readFile(wasmPath, null, function (error, data) {
      if (error) {
        reject(error);
        return;
      }

      resolve(data.buffer);
    });
  });
};

module.exports.sync = function readWasmSync() {
  var wasmPath = path.join(__dirname, "mappings.wasm");
  return fs.readFileSync(wasmPath).buffer;
};

module.exports.initialize = function (_) {
  console.debug("SourceMapConsumer.initialize is a no-op when running in node.js");
};